declare namespace setFunctionLength {
    type Func = (...args: unknown[]) => unknown;
}

/**
 * Sets the length property of a function.
 *
 * @param fn - The function to modify.
 * @param length - The new length of the function.
 * @param loose - Optional flag to allow non-configurable/writable length.
 * @returns The modified function with the new length.
 */
declare function setFunctionLength<T extends setFunctionLength.Func = setFunctionLength.Func>(
    fn: T,
    length: number,
    loose?: boolean
): T;

export = setFunctionLength;
